package variables;

public class Variables {

    public static void vars () {
        // TODO Auto-generated method stub

        // TIPOS DE DATO PRIMITIVO

        // Booleano : 1 bit : 2 valores
        boolean bool = false;

        // Byte : 1 byte : rango entre -128 y 127
        byte bytes = 21;

        // Short : 2 bytes : rango entre -32768 y 32767
        short shorty = 2105;

        // Int : 4 bytes entre -2 billones y 2 billones aprox
        int entero = 4939831;

        // long : 8 bytes : entre -9 y 9 quintillones
        long loong = 494191849;

        // float : 4 bytes de hasta 6 o 7 decimales
        float flooat = 3.1416926f;

        // double : 8 bytes : hasta 15 decimales
        double doble = 3.1415926535897;

        // char : 2 bytes : cualquier caracter ASCII/letra/valor
        char shar = 'A';

        // TIPO DE DATO POR REFERENCIA

        // String : tamano variable : secuencia de caracteres
        String texto = "GALENA ANTINATA REVIRA LODROGIL";

        // VARIABLE SWAPPING
        int d = 494;
        int a = 493;
        int temp;

        temp = d;
        d = a;
        a = temp;
        //swapped

        
    }

}
